import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');

const QueuePage = () => {
  const navigate = useNavigate();
  const [statusMessage, setStatusMessage] = useState('Click to join the queue.');

  useEffect(() => {
    socket.on('matchFound', (data) => {
      navigate(`/match/${data.matchId}`, { state: { self: data.self, opponent: data.opponent } });
    });

    socket.on('waiting', (data) => {
      setStatusMessage(data.message);
    });

    return () => {
      socket.off('matchFound');
      socket.off('waiting');
    };
  }, [navigate]);

  const handleJoinQueue = () => {
    const user = JSON.parse(sessionStorage.getItem('userData'));
    if (user) {
      socket.emit('joinQueue', {
        username: user.username,
        discordId: user.discordId,
        epicName: user.epicName || 'Guest',
      });
      setStatusMessage('You are in the queue. Waiting for another player...');
    } else {
      console.error('User not logged in.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>{statusMessage}</h2>
      <button onClick={handleJoinQueue} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Join Queue
      </button>
    </div>
  );
};

export default QueuePage;
