import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000'); // Backend URL

const MatchQueue = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Listen for matchFound event
    socket.on('matchFound', (data) => {
      console.log('Match Found:', data); // Debug log
      navigate(`/match/${data.matchId}`, { state: { opponent: data.opponent } }); // Navigate to MatchPage
    });

    return () => {
      socket.off('matchFound'); // Clean up the socket listener
    };
  }, [navigate]);

  const handleJoinQueue = () => {
    const user = JSON.parse(sessionStorage.getItem('userData'));
    if (user) {
      socket.emit('joinQueue', { username: user.username, avatarUrl: user.avatarUrl }); // Emit joinQueue event with user data
    } else {
      console.error('User not authenticated.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Join the Queue</h2>
      <button onClick={handleJoinQueue} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Join Queue
      </button>
    </div>
  );
};

export default MatchQueue;
