import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Login from './pages/Login';
import Account from './pages/Account';
import Dashboard from './pages/Dashboard';
import Tournaments from './pages/Tournaments';
import AdminDashboard from './pages/AdminDashboard';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(
    sessionStorage.getItem('isAuthenticated') === 'true'
  );
  const [isAdmin, setIsAdmin] = useState(
    sessionStorage.getItem('isAdmin') === 'true'
  );
  const [userData, setUserData] = useState(
    JSON.parse(sessionStorage.getItem('userData')) || null
  );

  useEffect(() => {
    // Check if query parameters exist in the URL
    const params = new URLSearchParams(window.location.search);
    const accessToken = params.get('accessToken');
    const username = params.get('username');
    const adminStatus = params.get('isAdmin') === 'true';

    if (accessToken) {
      // Set authentication state and save to session storage
      setIsAuthenticated(true);
      setIsAdmin(adminStatus);
      setUserData({ username, accessToken });

      sessionStorage.setItem('isAuthenticated', 'true');
      sessionStorage.setItem('isAdmin', adminStatus);
      sessionStorage.setItem('userData', JSON.stringify({ username, accessToken }));

      // Clear query parameters from the URL
      const cleanUrl = window.location.origin;
      window.history.replaceState(null, '', cleanUrl);
    }
  }, []);

  const handleLogout = () => {
    // Clear authentication state and session storage
    setIsAuthenticated(false);
    setIsAdmin(false);
    setUserData(null);
    sessionStorage.clear();
  };

  return (
    <Router>
      {isAuthenticated ? (
        <div style={{ display: 'flex' }}>
          <Sidebar onLogout={handleLogout} />
          <div style={{ flex: 1, padding: '20px' }}>
            <Routes>
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/tournaments" element={<Tournaments />} />
              <Route path="/account" element={<Account />} />
              {isAdmin && <Route path="/admin-dashboard" element={<AdminDashboard />} />}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </Routes>
          </div>
        </div>
      ) : (
        <Routes>
          <Route path="*" element={<Login />} />
        </Routes>
      )}
    </Router>
  );
};

export default App;
