import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

const socket = io('http://localhost:5000'); // Replace with your backend URL

const MatchPage = () => {
  const { matchId } = useParams();
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [player, setPlayer] = useState(null);
  const [opponent, setOpponent] = useState(null);

  useEffect(() => {
    const userData = JSON.parse(sessionStorage.getItem('userData'));
    const username = userData?.username || 'Guest';

    socket.emit('joinMatch', { matchId, username });

    socket.on('matchDetails', ({ player, opponent }) => {
      setPlayer(player);
      setOpponent(opponent);
    });

    socket.on('receiveMessage', (newMessage) => {
      setMessages((prevMessages) => [...prevMessages, newMessage]);
    });

    return () => {
      socket.disconnect();
    };
  }, [matchId]);

  const sendMessage = () => {
    if (message.trim()) {
      const newMessage = { username: player.username, content: message };
      socket.emit('sendMessage', { matchId, message: newMessage });
      setMessages((prevMessages) => [...prevMessages, newMessage]);
      setMessage('');
    }
  };

  return (
    <div className="match-page">
      <h2>MATCH PAGE</h2>
      <div className="players">
        <div className="player">
          <img src={player?.avatar || '/default-avatar.png'} alt="Your Avatar" />
          <h3>{player?.username || 'Your Username'}</h3>
        </div>
        <div className="vs">VS</div>
        <div className="player">
          <img src={opponent?.avatar || '/default-avatar.png'} alt="Opponent Avatar" />
          <h3>{opponent?.username || 'Opponent Username'}</h3>
        </div>
      </div>
      <div className="chat">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.username}:</strong> {msg.content}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
      <div className="actions">
        <button className="win-button">I Won!</button>
        <button className="lost-button">I Lost!</button>
        <button className="staff-button">Need Staff</button>
      </div>
    </div>
  );
};

export default MatchPage;
