import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [tournaments, setTournaments] = useState([]);
  const [newTournament, setNewTournament] = useState({
    title: '',
    description: '',
    date: '',
    maxPlayers: '',
    imageUrl: '',
  });
  const [message, setMessage] = useState('');

  const fetchTournaments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tournament/cups');
      setTournaments(response.data);
    } catch (error) {
      console.error('Failed to fetch tournaments:', error.message);
    }
  };

  const handleHostTournament = async () => {
    try {
      await axios.post('http://localhost:5000/tournament/host', newTournament);
      setMessage('Tournament hosted successfully!');
      fetchTournaments();
    } catch (error) {
      console.error('Failed to host tournament:', error.message);
      setMessage('Failed to host tournament.');
    }
  };

  const handleCancelTournament = async (tournamentId) => {
    try {
      await axios.post('http://localhost:5000/tournament/cancel', { tournamentId });
      setMessage('Tournament canceled successfully!');
      fetchTournaments();
    } catch (error) {
      console.error('Failed to cancel tournament:', error.message);
      setMessage('Failed to cancel tournament.');
    }
  };

  const handleDeleteTournament = async (tournamentId) => {
    try {
      await axios.post('http://localhost:5000/tournament/delete', { tournamentId });
      setMessage('Tournament deleted successfully!');
      fetchTournaments();
    } catch (error) {
      console.error('Failed to delete tournament:', error.message);
      setMessage('Failed to delete tournament.');
    }
  };

  useEffect(() => {
    fetchTournaments();
  }, []);

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      {message && <p className="message">{message}</p>}

      <div className="host-form">
        <h3>Host a New Tournament</h3>
        <input
          type="text"
          placeholder="Title"
          value={newTournament.title}
          onChange={(e) => setNewTournament({ ...newTournament, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={newTournament.description}
          onChange={(e) => setNewTournament({ ...newTournament, description: e.target.value })}
        />
        <input
          type="date"
          value={newTournament.date}
          onChange={(e) => setNewTournament({ ...newTournament, date: e.target.value })}
        />
        <input
          type="number"
          placeholder="Max Players"
          value={newTournament.maxPlayers}
          onChange={(e) => setNewTournament({ ...newTournament, maxPlayers: e.target.value })}
        />
        <input
          type="text"
          placeholder="Image URL (optional)"
          value={newTournament.imageUrl}
          onChange={(e) => setNewTournament({ ...newTournament, imageUrl: e.target.value })}
        />
        <button onClick={handleHostTournament}>Host Tournament</button>
      </div>

      <div className="tournament-list">
        <h3>Current Tournaments</h3>
        {tournaments.map((tournament) => (
          <div key={tournament._id} className="tournament-item">
            <h4>{tournament.title}</h4>
            <p>{tournament.description}</p>
            <button onClick={() => handleCancelTournament(tournament._id)}>Cancel</button>
            <button onClick={() => handleDeleteTournament(tournament._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
