import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Account = () => {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      const token = new URLSearchParams(window.location.search).get('accessToken');
      if (!token) return;

      try {
        const profileResponse = await axios.get('http://localhost:5000/auth/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(profileResponse.data);

        const adminResponse = await axios.get('http://localhost:5000/auth/isAdmin', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setIsAdmin(adminResponse.data.isAdmin);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div>
      {user ? (
        <div>
          <h2>Welcome, {user.username}</h2>
          <p>ID: {user.id}</p>
          <p>Admin Status: {isAdmin ? 'Admin' : 'User'}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Account;
