import React from 'react';
import { Link } from 'react-router-dom';
import './Sidebar.css';

const Sidebar = ({ onLogout }) => {
  // Retrieve isAdmin from session storage
  const userData = JSON.parse(sessionStorage.getItem('userData') || '{}');
  const isAdmin = userData.isAdmin === true || userData.isAdmin === 'true'; // Ensure it matches boolean or string

  console.log('Sidebar - isAdmin:', isAdmin); // Debugging: Check the value of isAdmin

  return (
    <div className="sidebar">
      <h1>souyyy</h1>
      <ul>
        <li><Link to="/dashboard">Dashboard</Link></li>
        <li><Link to="/tournaments">Tournaments</Link></li>
        <li><Link to="/account">Account</Link></li>
        {isAdmin && <li><Link to="/admin-dashboard">Admin Dashboard</Link>}</li>}
      </ul>
      <button onClick={onLogout}>Log Out</button>
    </div>
  );
};

export default Sidebar;
