import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [tournament, setTournament] = useState({
    name: '',
    description: '',
    startTime: '',
    endTime: '',
  });
  const [message, setMessage] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    // Retrieve isAdmin from sessionStorage
    const userData = JSON.parse(sessionStorage.getItem('userData') || '{}');
    setIsAdmin(userData.isAdmin === true || userData.isAdmin === 'true'); // Ensure it matches both boolean and string
  }, []);

  const handleChange = (e) => {
    setTournament({ ...tournament, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isAdmin) {
      setMessage('You do not have permission to host tournaments.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/tournament/create', {
        ...tournament,
        hostedBy: sessionStorage.getItem('userData')
          ? JSON.parse(sessionStorage.getItem('userData')).username
          : 'Unknown Admin',
      }, {
        headers: {
          isadmin: 'true', // Simulate admin authentication
        },
      });

      setMessage(response.data.message);
      setTournament({ name: '', description: '', startTime: '', endTime: '' });
    } catch (error) {
      console.error('Error hosting tournament:', error);
      setMessage('Failed to host tournament.');
    }
  };

  if (!isAdmin) {
    return <p>You do not have permission to access the Admin Dashboard.</p>;
  }

  return (
    <div>
      <h2>Admin Dashboard - Host a Tournament</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tournament Name</label>
          <input
            type="text"
            name="name"
            value={tournament.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={tournament.description}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Start Time</label>
          <input
            type="datetime-local"
            name="startTime"
            value={tournament.startTime}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>End Time</label>
          <input
            type="datetime-local"
            name="endTime"
            value={tournament.endTime}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Host Tournament</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AdminDashboard;
