import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Tournaments = () => {
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tournament/active');
        setTournaments(response.data);
      } catch (err) {
        console.error('Failed to fetch tournaments:', err.message);
      }
    };

    fetchTournaments();
  }, []);

  return (
    <div style={{ padding: '20px', color: 'white', backgroundColor: '#121212' }}>
      <h2>Active Tournaments</h2>
      <ul>
        {tournaments.map((tournament) => (
          <li key={tournament._id}>
            <p>
              {tournament.name} ({tournament.type}) | {new Date(tournament.startTime).toLocaleString()} -{' '}
              {new Date(tournament.endTime).toLocaleString()}
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tournaments;
