import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './QueuePage.css';

const socket = io('http://localhost:5000');

const QueuePage = () => {
  const { tournamentId } = useParams();
  const [statusMessage, setStatusMessage] = useState('Waiting for an opponent...');
  const navigate = useNavigate();

  useEffect(() => {
    const userData = JSON.parse(sessionStorage.getItem('userData'));
    const username = userData?.username || 'Guest';
    const discordAvatar = userData?.discordAvatar || 'default-avatar-url';
    const epicName = userData?.epicName || 'EpicGuest';

    socket.emit('joinQueue', { username, discordAvatar, epicName, tournamentId });

    socket.on('matchFound', ({ matchId, opponent }) => {
      setStatusMessage(`Match found! Redirecting...`);
      setTimeout(() => {
        navigate(`/match/${matchId}`, { state: { opponent, username, discordAvatar, epicName } });
      }, 3000);
    });

    socket.on('waiting', (data) => {
      setStatusMessage(data.message);
    });

    return () => {
      socket.emit('leaveQueue', { username, tournamentId });
      socket.disconnect();
    };
  }, [tournamentId, navigate]);

  const handleLeaveQueue = () => {
    navigate('/tournaments');
  };

  return (
    <div className="queue-page">
      <h2>Queue</h2>
      <p>{statusMessage}</p>
      <button onClick={handleLeaveQueue} className="leave-queue-button">
        Leave Queue
      </button>
    </div>
  );
};

export default QueuePage;
