import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import axios from 'axios';

const socket = io('http://localhost:5000');

const QueuePage = () => {
  const navigate = useNavigate();
  const { tournamentId } = useParams(); // Get tournament ID from URL
  const [statusMessage, setStatusMessage] = useState('Click to join the queue.');
  const [isInQueue, setIsInQueue] = useState(false);
  const [isInMatch, setIsInMatch] = useState(false);

  useEffect(() => {
    const user = JSON.parse(sessionStorage.getItem('userData'));
    if (!user) {
      navigate('/login');
      return;
    }

    // Check if user is already in a game or queue
    const checkUserStatus = async () => {
      try {
        const { data } = await axios.get('http://localhost:5000/matchmaking/status', {
          params: { username: user.username },
        });

        if (data.status === 'inGame') {
          setIsInMatch(true);
          setStatusMessage('⚠️ You are already in a match! Go to your current game.');
        } else if (data.status === 'inQueue') {
          setIsInQueue(true);
          setStatusMessage('✅ You are in the queue, waiting for a match.');
        }
      } catch (error) {
        console.error('Error checking user status:', error.message);
      }
    };

    checkUserStatus();

    // Listen for match found event
    socket.on('matchFound', (data) => {
      sessionStorage.setItem('currentMatch', JSON.stringify(data));
      navigate(`/match/${data.matchId}`, { state: { self: data.self, opponent: data.opponent } });
    });

    socket.on('alreadyInGame', () => {
      setStatusMessage('⚠️ You are already in a match! Go to your current game.');
      navigate('/current-game');
    });

    socket.on('alreadyInQueue', () => {
      setStatusMessage('✅ You are already in the queue, waiting for a match.');
    });

    socket.on('waiting', (data) => {
      setStatusMessage(data.message);
    });

    return () => {
      socket.off('matchFound');
      socket.off('alreadyInGame');
      socket.off('alreadyInQueue');
      socket.off('waiting');
    };
  }, [navigate]);

  const handleJoinQueue = async () => {
    const user = JSON.parse(sessionStorage.getItem('userData'));
    if (!user) {
      setStatusMessage('⚠️ You must be logged in to join the queue.');
      return;
    }

    try {
      const { data } = await axios.get('http://localhost:5000/matchmaking/status', {
        params: { username: user.username },
      });

      if (data.status === 'inGame') {
        setStatusMessage('⚠️ You are already in a match! Go to your current game.');
        navigate('/current-game');
      } else if (data.status === 'inQueue') {
        setStatusMessage('✅ You are already in the queue, waiting for a match.');
      } else {
        socket.emit('joinQueue', {
          username: user.username,
          discordId: user.discordId,
          epicName: user.epicName,
          tournamentId, // Use the actual tournament ID from the URL
        });
        setIsInQueue(true);
        setStatusMessage('✅ You are in the queue, waiting for a match.');
      }
    } catch (error) {
      console.error('Error joining queue:', error.message);
      setStatusMessage('⚠️ Error joining the queue.');
    }
  };

  const handleLeaveQueue = async () => {
    const user = JSON.parse(sessionStorage.getItem('userData'));
    if (!user) {
      return;
    }

    try {
      await axios.post('http://localhost:5000/matchmaking/leave-queue', {
        username: user.username,
      });

      setIsInQueue(false);
      setStatusMessage('ℹ️ You have left the queue.');
    } catch (error) {
      console.error('Error leaving queue:', error.message);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>{statusMessage}</h2>
      {!isInQueue && !isInMatch && (
        <button onClick={handleJoinQueue} className="join-button">
          Join Queue
        </button>
      )}
      {isInQueue && (
        <button onClick={handleLeaveQueue} className="leave-button">
          Leave Queue
        </button>
      )}
      {isInMatch && (
        <button onClick={() => navigate('/current-game')} className="current-game-button">
          Go to Current Game
        </button>
      )}
    </div>
  );
};

export default QueuePage;
