import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Tournaments.css';

const Tournaments = () => {
  const [tournaments, setTournaments] = useState([]);

  const fetchTournaments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tournament/cups');
      setTournaments(response.data);
    } catch (error) {
      console.error('Error fetching tournaments:', error.message);
    }
  };

  useEffect(() => {
    fetchTournaments();
  }, []);

  const handleJoin = async (tournamentId) => {
    try {
      const token = JSON.parse(sessionStorage.getItem('userData')).accessToken;
      const response = await axios.post(
        'http://localhost:5000/tournament/join',
        { tournamentId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert(response.data.message);
    } catch (error) {
      alert(error.response?.data?.message || 'Failed to join tournament');
    }
  };

  return (
    <div className="tournaments">
      <h2>Current Tournaments</h2>
      <div className="tournament-grid">
        {tournaments.map((tournament) => {
          const currentTime = new Date();
          const isJoinable =
            currentTime >= new Date(tournament.startTime) &&
            currentTime <= new Date(tournament.endTime) &&
            tournament.status === 'active';

          return (
            <div key={tournament._id} className="tournament-card">
              <img src={tournament.imageUrl} alt={tournament.title} className="tournament-image" />
              <h3>{tournament.title}</h3>
              <p>{tournament.description}</p>
              <p>
                <strong>Starts:</strong> {new Date(tournament.startTime).toLocaleString()}
              </p>
              <p>
                <strong>Ends:</strong> {new Date(tournament.endTime).toLocaleString()}
              </p>
              <button
                onClick={() => handleJoin(tournament._id)}
                disabled={!isJoinable}
                className={isJoinable ? 'join-button' : 'join-button disabled'}
              >
                {isJoinable ? 'Join Queue' : 'Unavailable'}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Tournaments;
