import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [tournamentName, setTournamentName] = useState('');
  const [tournamentType, setTournamentType] = useState('1vs1');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [tournaments, setTournaments] = useState([]);

  const fetchTournaments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/tournament/active');
      setTournaments(response.data);
    } catch (err) {
      console.error('Failed to fetch tournaments:', err.message);
    }
  };

  const handleHostTournament = async () => {
    try {
      await axios.post('http://localhost:5000/tournament/host', {
        name: tournamentName,
        type: tournamentType,
        startTime,
        endTime,
      });

      setTournamentName('');
      setTournamentType('1vs1');
      setStartTime('');
      setEndTime('');
      fetchTournaments();
    } catch (err) {
      console.error('Failed to host tournament:', err.message);
    }
  };

  const handleCancelTournament = async (id) => {
    try {
      await axios.post(`http://localhost:5000/tournament/cancel/${id}`);
      fetchTournaments();
    } catch (err) {
      console.error('Failed to cancel tournament:', err.message);
    }
  };

  useEffect(() => {
    fetchTournaments();
  }, []);

  return (
    <div style={{ padding: '20px', color: 'white', backgroundColor: '#121212' }}>
      <h2>Admin Dashboard</h2>

      <div style={{ marginBottom: '20px' }}>
        <h3>Host Tournament</h3>
        <input
          type="text"
          placeholder="Tournament Name"
          value={tournamentName}
          onChange={(e) => setTournamentName(e.target.value)}
          style={{ marginBottom: '10px', display: 'block' }}
        />
        <select
          value={tournamentType}
          onChange={(e) => setTournamentType(e.target.value)}
          style={{ marginBottom: '10px', display: 'block' }}
        >
          <option value="1vs1">1vs1</option>
          <option value="2vs2">2vs2</option>
          <option value="3vs3">3vs3</option>
          <option value="4vs4">4vs4</option>
        </select>
        <input
          type="datetime-local"
          placeholder="Start Time"
          value={startTime}
          onChange={(e) => setStartTime(e.target.value)}
          style={{ marginBottom: '10px', display: 'block' }}
        />
        <input
          type="datetime-local"
          placeholder="End Time"
          value={endTime}
          onChange={(e) => setEndTime(e.target.value)}
          style={{ marginBottom: '10px', display: 'block' }}
        />
        <button onClick={handleHostTournament}>Host Tournament</button>
      </div>

      <div>
        <h3>Active Tournaments</h3>
        <ul>
          {tournaments.map((tournament) => (
            <li key={tournament._id}>
              <p>
                {tournament.name} ({tournament.type}) | {new Date(tournament.startTime).toLocaleString()} -{' '}
                {new Date(tournament.endTime).toLocaleString()}
              </p>
              <button onClick={() => handleCancelTournament(tournament._id)}>Cancel</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
