import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

const socket = io('http://localhost:5000'); // Backend URL

const MatchPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { self, opponent } = location.state || {};
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    if (!self || !self.epicGamesName) {
      navigate('/tournaments');
      return;
    }

    const matchId = `${self.epicGamesName}-${opponent?.epicGamesName || 'waiting'}`;

    socket.emit('joinMatch', {
      matchId,
      username: self.epicGamesName, // Use Epic Games name
    });

    // Listen for incoming chat messages
    socket.on('receiveMessage', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.emit('leaveMatch', { matchId, username: self.epicGamesName });
      socket.off('receiveMessage');
    };
  }, [self, opponent, navigate]);

  const handleSendMessage = () => {
    if (newMessage.trim() && self) {
      const matchId = `${self.epicGamesName}-${opponent?.epicGamesName || 'waiting'}`;
      socket.emit('sendMessage', {
        matchId,
        message: newMessage,
        sender: self.epicGamesName, // Use Epic Games name as sender
      });
      setMessages((prev) => [
        ...prev,
        { sender: self.epicGamesName, message: newMessage },
      ]);
      setNewMessage('');
    }
  };

  return (
    <div className="match-page">
      <h1>Match Page</h1>
      <div className="players">
        <div className="player">
          <img
            src={self?.avatarUrl || '/default-avatar.png'}
            alt="Your Avatar"
            className="avatar"
          />
          <h3>{self?.epicGamesName || 'Your Name'}</h3>
        </div>
        <h2>VS</h2>
        <div className="player">
          <img
            src={opponent?.avatarUrl || '/default-avatar.png'}
            alt="Opponent Avatar"
            className="avatar"
          />
          <h3>{opponent?.epicGamesName || 'Waiting for opponent...'}</h3>
        </div>
      </div>

      <div className="chat">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.sender}:</strong> {msg.message}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default MatchPage;
