import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

const MatchPage = ({ match }) => {
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    socket.on('receiveMessage', (data) => {
      setChatMessages((prevMessages) => [...prevMessages, data]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      socket.emit('sendMessage', { sender: 'You', message: newMessage });
      setNewMessage('');
    }
  };

  return (
    <div>
      <h2>Match</h2>
      <div>
        <h3>{match.player1.playerName} vs {match.player2.playerName}</h3>
      </div>
      <div>
        <button>I Won!</button>
        <button>I Lost!</button>
        <button>Need Staff</button>
      </div>

      <div>
        <h3>Chat</h3>
        <div>
          {chatMessages.map((msg, idx) => (
            <p key={idx}><strong>{msg.sender}:</strong> {msg.message}</p>
          ))}
        </div>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
};

export default MatchPage;
