import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Tournaments = () => {
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tournament/all');
        setTournaments(response.data.filter((t) => t.isOpen));
      } catch (error) {
        console.error('Error fetching tournaments:', error);
      }
    };

    fetchTournaments();
  }, []);

  return (
    <div>
      <h2>Available Tournaments</h2>
      <ul>
        {tournaments.map((tournament) => (
          <li key={tournament._id}>
            <h3>{tournament.name}</h3>
            <p>{tournament.description}</p>
            <p>
              <strong>Start:</strong> {new Date(tournament.startTime).toLocaleString()}
            </p>
            <p>
              <strong>End:</strong> {new Date(tournament.endTime).toLocaleString()}
            </p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tournaments;
