import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './MatchPage.css';

const MatchPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { self, opponent } = location.state || {};
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const opponentPlaceholder = {
    avatarUrl: '/default-avatar.png',
    epicName: 'Waiting for opponent...',
  };

  useEffect(() => {
    if (!self) {
      navigate('/tournaments');
    }
  }, [self, navigate]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      setMessages((prev) => [...prev, { sender: self.epicName, message: newMessage }]);
      setNewMessage('');
    }
  };

  const handleLeaveMatch = () => {
    navigate('/tournaments');
  };

  return (
    <div className="match-page">
      <h1>Match Page</h1>
      <div className="players">
        <div className="player">
          <img
            src={self?.avatarUrl || '/default-avatar.png'}
            alt="Your Avatar"
            className="avatar"
          />
          <h3>{self?.epicName || 'Your Name'}</h3>
        </div>
        <h2>VS</h2>
        <div className="player">
          <img
            src={opponent?.avatarUrl || opponentPlaceholder.avatarUrl}
            alt="Opponent Avatar"
            className="avatar"
          />
          <h3>{opponent?.epicName || opponentPlaceholder.epicName}</h3>
        </div>
      </div>

      <div className="actions">
        <button className="win-button">I Won</button>
        <button className="lost-button">I Lost</button>
        <button className="staff-button">Need Staff</button>
      </div>

      <div className="chat">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.sender}:</strong> {msg.message}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>

      <button className="leave-match" onClick={handleLeaveMatch}>
        Leave Match
      </button>
    </div>
  );
};

export default MatchPage;
