import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

// Initialize socket outside the component to avoid reinitialization
const socket = io('http://localhost:5000');

const MatchPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { self, opponent } = location.state || {};
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // Placeholder for opponent if not found
  const opponentPlaceholder = {
    avatarUrl: '/default-avatar.png',
    epicName: 'Waiting for opponent...',
  };

  useEffect(() => {
    // If self data is not available, redirect back to tournaments
    if (!self) {
      navigate('/tournaments');
    }

    // Join the match room
    socket.emit('joinMatch', { matchId: `${self?.id}-${opponent?.id}` });

    // Listen for incoming chat messages
    socket.on('receiveMessage', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      // Clean up socket listeners
      socket.off('receiveMessage');
    };
  }, [self, opponent, navigate]);

  const handleSendMessage = () => {
    if (newMessage.trim() && self) {
      const matchId = `${self.id}-${opponent?.id}`;
      const messageData = {
        matchId,
        message: newMessage,
        sender: self.epicName, // Use self.epicName for the sender
      };
      socket.emit('sendMessage', messageData);
      setMessages((prev) => [...prev, messageData]); // Add to local messages
      setNewMessage(''); // Clear the input field
    }
  };

  const handleLeaveMatch = () => {
    navigate('/tournaments');
  };

  return (
    <div className="match-page">
      <h1>Match Page</h1>
      <div className="players">
        {/* Player 1 (self) */}
        <div className="player">
          <img
            src={self?.avatarUrl || '/default-avatar.png'}
            alt="Your Avatar"
            className="avatar"
          />
          <h3>{self?.epicName || 'Your Name'}</h3>
        </div>
        <h2>VS</h2>
        {/* Player 2 (opponent) */}
        <div className="player">
          <img
            src={opponent?.avatarUrl || opponentPlaceholder.avatarUrl}
            alt="Opponent Avatar"
            className="avatar"
          />
          <h3>{opponent?.epicName || opponentPlaceholder.epicName}</h3>
        </div>
      </div>

      <div className="actions">
        <button className="win-button">I Won</button>
        <button className="lost-button">I Lost</button>
        <button className="staff-button">Need Staff</button>
      </div>

      <div className="chat">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.sender}:</strong> {msg.message}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>

      <button className="leave-match" onClick={handleLeaveMatch}>
        Leave Match
      </button>
    </div>
  );
};

export default MatchPage;
