import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

const socket = io('http://localhost:5000');

const MatchPage = () => {
  const { state } = useLocation();
  const { opponent, username, discordAvatar, epicName } = state;
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const sendMessage = () => {
    if (message.trim() !== '') {
      const newMessage = { sender: username, text: message };
      setMessages([...messages, newMessage]);
      setMessage('');
      socket.emit('sendMessage', newMessage);
    }
  };

  socket.on('receiveMessage', (newMessage) => {
    setMessages((prevMessages) => [...prevMessages, newMessage]);
  });

  const handleWin = () => alert('You reported a win!');
  const handleLoss = () => alert('You reported a loss!');
  const handleNeedStaff = () => alert('A staff member has been notified!');

  return (
    <div className="match-page">
      <div className="players-info">
        <div className="player">
          <img src={discordAvatar} alt="Your Avatar" />
          <p>{epicName}</p>
        </div>
        <p>VS</p>
        <div className="player">
          <img src={opponent.discordAvatar} alt="Opponent Avatar" />
          <p>{opponent.epicName}</p>
        </div>
      </div>

      <div className="actions">
        <button onClick={handleWin}>I Won</button>
        <button onClick={handleLoss}>I Lost</button>
        <button onClick={handleNeedStaff}>Need Staff</button>
      </div>

      <div className="chat">
        <div className="messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.sender}:</strong> {msg.text}
            </p>
          ))}
        </div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default MatchPage;
