import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

const socket = io('http://localhost:5000'); // Backend URL

const MatchPage = () => {
  const { matchId } = useParams();
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');
  const [opponent, setOpponent] = useState(null);
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    const userData = JSON.parse(sessionStorage.getItem('userData'));
    const username = userData?.username || 'Guest';
    const avatarUrl = `https://cdn.discordapp.com/avatars/${userData.discordId}/${userData.discordId}.png`;

    setPlayer({ username, avatarUrl });

    socket.emit('joinMatch', { matchId, username, avatarUrl });

    socket.on('matchFound', ({ opponent }) => {
      setOpponent(opponent);
    });

    socket.on('receiveMessage', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    return () => {
      socket.disconnect();
    };
  }, [matchId]);

  const handleSendMessage = () => {
    socket.emit('sendMessage', { matchId, message });
    setMessages((prev) => [...prev, { sender: player.username, message }]);
    setMessage('');
  };

  return (
    <div className="match-page">
      <div className="players-info">
        <div className="player">
          <img src={player?.avatarUrl} alt="Your Avatar" className="avatar" />
          <p>{player?.username}</p>
        </div>
        <p>VS</p>
        <div className="player">
          <img src={opponent?.avatarUrl} alt="Opponent Avatar" className="avatar" />
          <p>{opponent?.username}</p>
        </div>
      </div>

      <div className="chat-section">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div key={index}>
              <strong>{msg.sender}: </strong>
              {msg.message}
            </div>
          ))}
        </div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type a message..."
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>

      <div className="match-actions">
        <button className="win-button">I Won</button>
        <button className="lost-button">I Lost</button>
        <button className="staff-button">Need Staff</button>
      </div>
    </div>
  );
};

export default MatchPage;
