import React, { useState } from 'react';
import axios from 'axios';

const MatchQueue = () => {
  const [message, setMessage] = useState('');
  const [match, setMatch] = useState(null);

  const handleJoinQueue = async () => {
    try {
      const response = await axios.post('http://localhost:5000/matchmaking/join', {
        playerId: 'player1-id', // Replace with actual player ID
        playerName: 'Player 1', // Replace with actual player name
      });

      setMessage(response.data.message);

      if (response.data.match) {
        setMatch(response.data.match);
      }
    } catch (error) {
      console.error('Error joining queue:', error.message);
      setMessage('Failed to join queue.');
    }
  };

  if (match) {
    return (
      <div>
        <h2>Match Found!</h2>
        <p>{match.player1.playerName} vs {match.player2.playerName}</p>
        <a href={`/match/${match.player1.playerId}-${match.player2.playerId}`}>Go to Match Page</a>
      </div>
    );
  }

  return (
    <div>
      <button onClick={handleJoinQueue}>Join Queue</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default MatchQueue;
