import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './QueuePage.css';

const socket = io('http://localhost:5000'); // Backend URL

const QueuePage = () => {
  const { tournamentId } = useParams();
  const [statusMessage, setStatusMessage] = useState('Waiting for an opponent...');
  const navigate = useNavigate();

  useEffect(() => {
    const userData = JSON.parse(sessionStorage.getItem('userData'));
    const username = userData?.username || 'Guest';

    // Join the queue
    socket.emit('joinQueue', { username, tournamentId });

    // Listen for match found
    socket.on('matchFound', ({ matchId, opponent }) => {
      setStatusMessage(`Match found! Your opponent is ${opponent.username}`);
      setTimeout(() => {
        navigate(`/match/${matchId}`); // Redirect to the match page
      }, 3000);
    });

    // Listen for waiting status
    socket.on('waiting', (data) => {
      setStatusMessage(data.message);
    });

    // Cleanup on component unmount
    return () => {
      socket.emit('leaveQueue', { username, tournamentId });
      socket.disconnect();
    };
  }, [tournamentId, navigate]);

  const handleLeaveQueue = () => {
    navigate('/tournaments');
  };

  return (
    <div className="queue-page">
      <h2>Queue</h2>
      <p>{statusMessage}</p>
      <button onClick={handleLeaveQueue} className="leave-queue-button">
        Leave Queue
      </button>
    </div>
  );
};

export default QueuePage;
