import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

const socket = io('http://localhost:5000'); // Backend URL

const MatchPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { self, opponent } = location.state || {};
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const opponentPlaceholder = {
    avatarUrl: '/default-avatar.png',
    epicName: 'Waiting for opponent...',
  };

  useEffect(() => {
    if (!self) {
      navigate('/tournaments');
      return;
    }

    // Join the match room
    const matchId = `${self.id}-${opponent?.id || 'waiting'}`;
    socket.emit('joinMatch', { matchId, username: self.epicName });

    // Listen for match data and messages
    socket.on('receiveMessage', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.emit('leaveMatch', { matchId, username: self.epicName });
      socket.off('receiveMessage');
    };
  }, [self, opponent, navigate]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const matchId = `${self.id}-${opponent?.id || 'waiting'}`;
      socket.emit('sendMessage', {
        matchId,
        message: newMessage,
        sender: self.epicName,
      });
      setMessages((prev) => [
        ...prev,
        { sender: self.epicName, message: newMessage },
      ]);
      setNewMessage('');
    }
  };

  return (
    <div className="match-page">
      <h1>Match Page</h1>
      <div className="players">
        <div className="player">
          <img
            src={
              self?.avatarUrl
                ? `https://cdn.discordapp.com/avatars/${self.discordId}/${self.avatarUrl}.png`
                : '/default-avatar.png'
            }
            alt="Your Avatar"
            className="avatar"
          />
          <h3>{self?.epicName || 'Your Name'}</h3>
        </div>
        <h2>VS</h2>
        <div className="player">
          <img
            src={
              opponent?.avatarUrl
                ? `https://cdn.discordapp.com/avatars/${opponent.discordId}/${opponent.avatarUrl}.png`
                : opponentPlaceholder.avatarUrl
            }
            alt="Opponent Avatar"
            className="avatar"
          />
          <h3>{opponent?.epicName || opponentPlaceholder.epicName}</h3>
        </div>
      </div>

      <div className="chat">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.sender}:</strong> {msg.message}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default MatchPage;
