import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Tournaments = () => {
  const [activeTournaments, setActiveTournaments] = useState([]);
  const [pastTournaments, setPastTournaments] = useState([]);
  const [showPast, setShowPast] = useState(false);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const activeResponse = await axios.get('http://localhost:5000/tournament/cups');
        setActiveTournaments(activeResponse.data);

        const pastResponse = await axios.get('http://localhost:5000/tournament/past-cups');
        setPastTournaments(pastResponse.data);
      } catch (err) {
        console.error('Failed to fetch tournaments:', err.message);
      }
    };

    fetchTournaments();
  }, []);

  return (
    <div style={{ padding: '20px', color: 'white', backgroundColor: '#121212' }}>
      <h2>Tournaments</h2>
      <div>
        <button onClick={() => setShowPast(false)}>All Cups</button>
        <button onClick={() => setShowPast(true)}>Past Cups</button>
      </div>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {(showPast ? pastTournaments : activeTournaments).map((tournament) => (
          <div
            key={tournament._id}
            style={{
              border: '1px solid #ccc',
              borderRadius: '10px',
              padding: '10px',
              maxWidth: '300px',
              backgroundColor: '#1c1c1c',
            }}
          >
            <img
              src={tournament.image}
              alt={tournament.name}
              style={{ width: '100%', borderRadius: '10px' }}
            />
            <h3>{tournament.name}</h3>
            <p>
              {new Date(tournament.startTime).toLocaleString()} -{' '}
              {new Date(tournament.endTime).toLocaleString()}
            </p>
            <p>Type: {tournament.type}</p>
            {showPast ? null : (
              <>
                <button style={{ marginRight: '10px' }}>Join Queue</button>
                <button>Leaderboard</button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tournaments;
