import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CurrentGame = () => {
  const navigate = useNavigate();
  const [isInMatch, setIsInMatch] = useState(false);
  const [matchData, setMatchData] = useState(null);

  useEffect(() => {
    const currentMatch = JSON.parse(sessionStorage.getItem('currentMatch'));

    if (currentMatch) {
      setIsInMatch(true);
      setMatchData(currentMatch);
    } else {
      setIsInMatch(false);
    }
  }, []);

  const handleLeaveMatch = async () => {
    const user = JSON.parse(sessionStorage.getItem('userData'));

    if (!user || !matchData) {
      navigate('/tournaments');
      return;
    }

    try {
      // Notify the backend that the player has left the match
      await axios.post('http://localhost:5000/matchmaking/leave-match', {
        username: user.username,
      });

      // Remove match data from session storage
      sessionStorage.removeItem('currentMatch');

      // Redirect to tournaments page
      navigate('/tournaments');
    } catch (error) {
      console.error('Error leaving match:', error);
    }
  };

  return (
    <div>
      {isInMatch ? (
        <div className="match-container">
          <h2>You're currently in a match!</h2>
          <button onClick={() => navigate(`/match/${matchData.matchId}`)}>Go to Match</button>
          <button onClick={handleLeaveMatch} className="leave-button">Leave Match</button>
        </div>
      ) : (
        <div className="popup-container">
          <div className="popup">
            <h3>You are currently not participating in any active game</h3>
            <button onClick={() => navigate('/tournaments')}>Ok</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CurrentGame;
