import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Tournaments.css';

const Tournaments = () => {
  const [activeTournaments, setActiveTournaments] = useState([]);
  const [pastTournaments, setPastTournaments] = useState([]);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const activeResponse = await axios.get('http://localhost:5000/tournament/cups');
        setActiveTournaments(activeResponse.data);

        const pastResponse = await axios.get('http://localhost:5000/tournament/past');
        setPastTournaments(pastResponse.data);
      } catch (error) {
        console.error('Error fetching tournaments:', error.message);
      }
    };

    fetchTournaments();
  }, []);

  const handleDeletePastTournament = async (tournamentId) => {
    try {
      await axios.delete('http://localhost:5000/tournament/delete', {
        data: { tournamentId },
      });
      setPastTournaments((prev) => prev.filter((t) => t._id !== tournamentId));
    } catch (error) {
      console.error('Error deleting past tournament:', error.message);
    }
  };

  return (
    <div className="tournaments-container">
      <h2>All Cups</h2>
      <div className="tournament-list">
        {activeTournaments.map((tournament) => (
          <div key={tournament._id} className="tournament-card">
            <h3>{tournament.title}</h3>
            <p>{tournament.description}</p>
            <button className="join-button">Join Queue</button>
            <button className="leaderboard-button">Leaderboard</button>
          </div>
        ))}
      </div>

      <h2>Past Cups</h2>
      <div className="tournament-list">
        {pastTournaments.map((tournament) => (
          <div key={tournament._id} className="tournament-card">
            <h3>{tournament.title}</h3>
            <p>{tournament.description}</p>
            <button className="leaderboard-button">Leaderboard</button>
            <button onClick={() => handleDeletePastTournament(tournament._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tournaments;
