import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Account = () => {
  const [userData, setUserData] = useState(null);
  const [fortniteName, setFortniteName] = useState('');
  const [lastUpdated, setLastUpdated] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const token = sessionStorage.getItem('userData')
        ? JSON.parse(sessionStorage.getItem('userData')).accessToken
        : null;

      if (!token) return;

      try {
        const response = await axios.get('http://localhost:5000/account', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUserData(response.data);
        setFortniteName(response.data.fortniteName || '');
        setLastUpdated(response.data.fortniteLastUpdated || null);
      } catch (err) {
        console.error('Error fetching user data:', err);
      }
    };

    fetchData();
  }, []);

  const handleFortniteNameSubmit = async () => {
    const token = sessionStorage.getItem('userData')
      ? JSON.parse(sessionStorage.getItem('userData')).accessToken
      : null;

    if (!token) return;

    if (!fortniteName.trim()) {
      setError('Fortnite name cannot be empty.');
      return;
    }

    const oneWeekAgo = new Date();
    oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);

    if (lastUpdated && new Date(lastUpdated) > oneWeekAgo) {
      setError('You can only update your Fortnite name once every 7 days.');
      return;
    }

    try {
      const response = await axios.post(
        'http://localhost:5000/account/update-fortnite-name',
        { fortniteName },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setSuccess('Fortnite name updated successfully!');
      setLastUpdated(response.data.lastUpdated);
      setError('');
    } catch (err) {
      console.error('Error updating Fortnite name:', err);
      setError('Failed to update Fortnite name. Please try again.');
    }
  };

  if (!userData) return <p>Loading...</p>;

  return (
    <div>
      <h2>Account</h2>
      <p><strong>Discord Name:</strong> {userData.discordName}</p>
      <p><strong>Discord ID:</strong> {userData.discordId}</p>
      <p><strong>Wins:</strong> {userData.wins || 0}</p>
      <p><strong>Losses:</strong> {userData.losses || 0}</p>
      <p>
        <strong>Fortnite Name:</strong> {fortniteName || 'Not Set'}
      </p>
      <p>
        <strong>Last Updated:</strong> {lastUpdated ? new Date(lastUpdated).toLocaleDateString() : 'Never'}
      </p>

      <div style={{ marginTop: '20px' }}>
        <input
          type="text"
          placeholder="Enter Fortnite Name"
          value={fortniteName}
          onChange={(e) => setFortniteName(e.target.value)}
          disabled={lastUpdated && new Date(lastUpdated) > new Date(new Date().setDate(new Date().getDate() - 7))}
        />
        <button onClick={handleFortniteNameSubmit}>Save</button>
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </div>
  );
};

export default Account;
