import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [currentTournaments, setCurrentTournaments] = useState([]);
  const [pastTournaments, setPastTournaments] = useState([]);

  const fetchTournaments = async () => {
    try {
      const currentResponse = await axios.get('http://localhost:5000/tournament/cups');
      const pastResponse = await axios.get('http://localhost:5000/tournament/past');
      setCurrentTournaments(currentResponse.data);
      setPastTournaments(pastResponse.data);
    } catch (error) {
      console.error('Error fetching tournaments:', error.message);
    }
  };

  useEffect(() => {
    fetchTournaments();
  }, []);

  const handleCancelTournament = async (tournamentId) => {
    try {
      await axios.post('http://localhost:5000/tournament/cancel', { tournamentId });
      fetchTournaments();
    } catch (error) {
      console.error('Error canceling tournament:', error.message);
    }
  };

  const handleDeleteTournament = async (tournamentId) => {
    try {
      await axios.delete(`http://localhost:5000/tournament/${tournamentId}`);
      fetchTournaments();
    } catch (error) {
      console.error('Error deleting tournament:', error.message);
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard - Current Tournaments</h2>
      <div className="tournament-list">
        {currentTournaments.map((tournament) => (
          <div key={tournament._id} className="tournament-card">
            <h3>{tournament.title}</h3>
            <p>{tournament.description}</p>
            <button onClick={() => handleCancelTournament(tournament._id)}>Cancel</button>
          </div>
        ))}
      </div>

      <h2>Admin Dashboard - Past Tournaments</h2>
      <div className="tournament-list">
        {pastTournaments.map((tournament) => (
          <div key={tournament._id} className="tournament-card">
            <h3>{tournament.title}</h3>
            <p>{tournament.description}</p>
            <button onClick={() => handleDeleteTournament(tournament._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
