import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Tournaments.css';

const Tournaments = () => {
  const [activeTournaments, setActiveTournaments] = useState([]);
  const [pastTournaments, setPastTournaments] = useState([]);
  const [view, setView] = useState('active'); // 'active' or 'past'

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const activeResponse = await axios.get('http://localhost:5000/tournament/active');
        setActiveTournaments(activeResponse.data);

        const pastResponse = await axios.get('http://localhost:5000/tournament/past');
        setPastTournaments(pastResponse.data);
      } catch (error) {
        console.error('Error fetching tournaments:', error.message);
      }
    };

    fetchTournaments();
  }, []);

  const renderTournaments = (tournaments) => {
    return tournaments.map((tournament) => (
      <div key={tournament._id} className="tournament-card">
        <h3>{tournament.title} [{new Date(tournament.startTime).toLocaleDateString()}]</h3>
        <p>{tournament.description}</p>
        <p>Type: {tournament.type}</p>
        <div className="tournament-actions">
          <button className="view-btn">View</button>
          <button className="leaderboard-btn">Leaderboard</button>
        </div>
      </div>
    ));
  };

  return (
    <div className="tournaments-container">
      <div className="tabs">
        <button
          className={view === 'active' ? 'active' : ''}
          onClick={() => setView('active')}
        >
          Cups
        </button>
        <button
          className={view === 'past' ? 'active' : ''}
          onClick={() => setView('past')}
        >
          Past Cups
        </button>
      </div>
      <div className="tournament-list">
        {view === 'active' ? renderTournaments(activeTournaments) : renderTournaments(pastTournaments)}
      </div>
    </div>
  );
};

export default Tournaments;
