// MatchPage.js (Frontend)
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import './MatchPage.css';

const socket = io('http://localhost:5000');

const MatchPage = () => {
  const { matchId } = useParams();
  const navigate = useNavigate();
  const [matchData, setMatchData] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    const userData = JSON.parse(sessionStorage.getItem('userData'));
    const username = userData?.username || 'Guest';

    // Join the match room
    socket.emit('joinMatch', { matchId, username });

    // Listen for match data
    socket.on('matchData', (data) => {
      setMatchData(data);
    });

    // Listen for new chat messages
    socket.on('receiveMessage', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    return () => {
      socket.emit('leaveMatch', { matchId, username });
      socket.disconnect();
    };
  }, [matchId]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      socket.emit('sendMessage', {
        matchId,
        message: newMessage,
        sender: matchData?.self.username,
      });
      setNewMessage('');
    }
  };

  const handleLeaveMatch = () => {
    navigate('/tournaments');
  };

  if (!matchData) {
    return <div>Loading match data...</div>;
  }

  return (
    <div className="match-page">
      <h1>Match Page</h1>
      <div className="players">
        <div className="player">
          <img
            src={matchData.self.avatarUrl || '/default-avatar.png'}
            alt="Your Avatar"
            className="avatar"
          />
          <h3>{matchData.self.epicName}</h3>
        </div>
        <h2>VS</h2>
        <div className="player">
          <img
            src={matchData.opponent.avatarUrl || '/default-avatar.png'}
            alt="Opponent Avatar"
            className="avatar"
          />
          <h3>{matchData.opponent.epicName}</h3>
        </div>
      </div>

      <div className="actions">
        <button className="win-button">I Won</button>
        <button className="lost-button">I Lost</button>
        <button className="staff-button">Need Staff</button>
      </div>

      <div className="chat">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <p key={index}>
              <strong>{msg.sender}:</strong> {msg.message}
            </p>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>

      <button className="leave-match" onClick={handleLeaveMatch}>
        Leave Match
      </button>
    </div>
  );
};

export default MatchPage;
