import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Tournaments from './pages/Tournaments';
import Account from './pages/Account';
import AdminDashboard from './pages/AdminDashboard';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    // Check if query parameters exist in the URL
    const params = new URLSearchParams(window.location.search);
    const accessToken = params.get('accessToken');
    const username = params.get('username');
    const adminStatus = params.get('isAdmin') === 'true'; // Fetch admin status from query if available

    if (accessToken && username) {
      // Store user data in sessionStorage
      sessionStorage.setItem(
        'userData',
        JSON.stringify({
          accessToken,
          username,
          isAdmin: adminStatus,
        })
      );

      // Update state
      setIsAuthenticated(true);
      setIsAdmin(adminStatus);

      // Remove query parameters from URL
      window.history.replaceState({}, document.title, '/dashboard');
    } else {
      // Check sessionStorage for existing authentication
      const userData = sessionStorage.getItem('userData');
      if (userData) {
        const parsedData = JSON.parse(userData);
        setIsAuthenticated(true);
        setIsAdmin(parsedData.isAdmin);
      }
    }
  }, []);

  const handleLogout = () => {
    // Clear session storage and reset state
    sessionStorage.clear();
    setIsAuthenticated(false);
    setIsAdmin(false);
  };

  return (
    <Router>
      {isAuthenticated ? (
        <div style={{ display: 'flex', minHeight: '100vh' }}>
          <Sidebar onLogout={handleLogout} />
          <div style={{ flex: 1, padding: '20px', backgroundColor: '#f4f4f4' }}>
            <Routes>
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/tournaments" element={<Tournaments />} />
              <Route path="/account" element={<Account />} />
              {isAdmin && (
                <Route path="/admin-dashboard" element={<AdminDashboard />} />
              )}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </Routes>
          </div>
        </div>
      ) : (
        <Routes>
          <Route path="*" element={<Login />} />
        </Routes>
      )}
    </Router>
  );
};

export default App;
