import React, { useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [tournament, setTournament] = useState({
    name: '',
    description: '',
    startTime: '',
    endTime: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setTournament({ ...tournament, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const isAdmin = sessionStorage.getItem('isAdmin') === 'true';

    if (!isAdmin) {
      setMessage('You do not have permission to host tournaments.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/tournament/create', {
        ...tournament,
        hostedBy: sessionStorage.getItem('userData')
          ? JSON.parse(sessionStorage.getItem('userData')).username
          : 'Unknown Admin',
      }, {
        headers: {
          isadmin: 'true', // Simulating admin authentication
        },
      });

      setMessage(response.data.message);
    } catch (error) {
      console.error('Error hosting tournament:', error);
      setMessage('Failed to host tournament.');
    }
  };

  return (
    <div>
      <h2>Host a Tournament</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Tournament Name"
          value={tournament.name}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="Tournament Description"
          value={tournament.description}
          onChange={handleChange}
        />
        <input
          type="datetime-local"
          name="startTime"
          value={tournament.startTime}
          onChange={handleChange}
          required
        />
        <input
          type="datetime-local"
          name="endTime"
          value={tournament.endTime}
          onChange={handleChange}
          required
        />
        <button type="submit">Host Tournament</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AdminDashboard;
