import React from 'react';
import { Link } from 'react-router-dom';
import './Sidebar.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

const Sidebar = ({ onLogout }) => {
  const userData = JSON.parse(sessionStorage.getItem('userData') || '{}');
  const isAuthenticated = !!userData.accessToken;

  const handleLogin = () => {
    const clientId = process.env.REACT_APP_DISCORD_CLIENT_ID;
    const redirectUri = encodeURIComponent(process.env.REACT_APP_DISCORD_REDIRECT_URL);

    if (!clientId || !redirectUri) {
      console.error('Environment variables for Discord login are not properly set!');
      return;
    }

    const discordAuthUrl = `https://discord.com/api/oauth2/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=code&scope=identify`;
    window.location.href = discordAuthUrl;
  };

  return (
    <div className="sidebar">
      <h1>NOBLE CREATIVE</h1>
      <ul>
        <li>
          <Link to="/tournaments">
            <i className="fas fa-trophy"></i> Tournaments
          </Link>
        </li>
        <li>
          <Link to="/current-game">
            <i className="fas fa-gamepad"></i> Current Game
          </Link>
        </li>
        <li>
          <Link to="/account">
            <i className="fas fa-user"></i> Account
          </Link>
        </li>
      </ul>
      {!isAuthenticated && (
        <button onClick={handleLogin} className="login-button">
          <i className="fab fa-discord"></i> Sign in with Discord
        </button>
      )}
      {isAuthenticated && (
        <button onClick={onLogout} className="logout-button">
          Log Out
        </button>
      )}
    </div>
  );
};

export default Sidebar;
