import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Tournaments.css';

const Tournaments = () => {
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const fetchTournaments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tournament/all');
        setTournaments(response.data.filter((t) => t.isOpen));
      } catch (error) {
        console.error('Error fetching tournaments:', error);
      }
    };

    fetchTournaments();
  }, []);

  const handlePlayNow = async (tournamentId) => {
    try {
      const userData = JSON.parse(sessionStorage.getItem('userData'));
      const response = await axios.post(`http://localhost:5000/tournament/${tournamentId}/join-queue`, {
        userId: userData.accessToken,
        username: userData.username,
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error joining queue:', error);
      alert('Failed to join queue.');
    }
  };

  const handleViewLeaderboard = (tournamentId) => {
    window.location.href = `/tournament/${tournamentId}/leaderboard`;
  };

  return (
    <div className="tournaments-page">
      <h2>Available Tournaments</h2>
      <div className="tournament-list">
        {tournaments.map((tournament) => (
          <div className="tournament-card" key={tournament._id}>
            <h3>{tournament.name}</h3>
            <p>{tournament.description}</p>
            <p>
              <strong>Start:</strong> {new Date(tournament.startTime).toLocaleString()}
            </p>
            <p>
              <strong>End:</strong> {new Date(tournament.endTime).toLocaleString()}
            </p>
            <div className="tournament-actions">
              <button className="play-now-btn" onClick={() => handlePlayNow(tournament._id)}>
                Play Now
              </button>
              <button className="leaderboard-btn" onClick={() => handleViewLeaderboard(tournament._id)}>
                Leaderboard
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tournaments;
